import React from 'react';

function Cachipun(props) {
  return (
    <div>
      <div>Tu puntuacion es:{props.score}</div>
      {props.result === 'win' && (
        <h2>
          Ganaste! Tu Elegiste {props.myChoice} y el pc eligió {props.pcChoice}
        </h2>
      )}
      {props.result === 'lose' && (
        <h2>
          Perdiste! Tu Elegiste {props.myChoice} y el pc eligió {props.pcChoice}
        </h2>
      )}
      {props.result === 'draw' && (
        <h2>
          Empataron! Tu Elegiste {props.myChoice} y el pc eligió{' '}
          {props.pcChoice}
        </h2>
      )}
      <div>
        <button value="piedra" onClick={props.onAddOption}>
          Piedra
        </button>
      </div>
      <div>
        <button value="papel" onClick={props.onAddOption}>
          Papel
        </button>
      </div>
      <div>
        <button value="tijera" onClick={props.onAddOption}>
          Tijera
        </button>
      </div>
    </div>
  );
}

export default Cachipun;
