import React, {useState} from 'react';
import CachipunContainer from './CachipunContainer';

export default function CachipunEvaluation(props) {

    const [gameCounter, setGameCounter] = useState(0)
    const [winGame, setWinGame] = useState(1)
    const [score, setScore] = useState(0)

    const gameCounterHandler = (e) => {
        setGameCounter(gameCounter + 1)
        setScore(score + e)
        if (gameCounter >= 5 && score <= 2) {
            setWinGame(2)
        } else if (gameCounter >= 5 && score >= 3) {
            setWinGame(3)
        }
    }

  return (
    <div>
        {winGame === 1 && <CachipunContainer onAddGameCounter={gameCounterHandler} />}
        {winGame === 2 && <h2>Ganaste la partida!</h2>}
        {winGame === 3 && <h2>Perdiste la partida!</h2>}
    </div>
  );
}
