import React from 'react';
import { Button } from 'react-bootstrap';

function Cachipun(props) {
  return (
    <div>
      <div>Tu puntuacion es:{props.score}</div>
      {props.result === 'win' && (
        <h2>
          Ganaste! Tu Elegiste {props.myChoice} y el pc eligió {props.pcChoice}
        </h2>
      )}
      {props.result === 'lose' && (
        <h2>
          Perdiste! Tu Elegiste {props.myChoice} y el pc eligió {props.pcChoice}
        </h2>
      )}
      {props.result === 'draw' && (
        <h2>
          Empataron! Tu Elegiste {props.myChoice} y el pc eligió{' '}
          {props.pcChoice}
        </h2>
      )}
      <div>
        <Button variant="outline-primary" value="piedra" size="lg" active onClick={props.onAddOption}>
          Piedra
        </Button>
      </div>
      <div>
        <Button variant="outline-primary" value="papel" onClick={props.onAddOption}>
          Papel
        </Button>
      </div>
      <div>
        <Button variant="outline-primary" value="tijera" onClick={props.onAddOption}>
          Tijera
        </Button>
      </div>
    </div>
  );
}

export default Cachipun;
