import React from "react";
import { Button, Alert } from "react-bootstrap";

function Cachipun(props) {
  return (
    <div>
      <div>Tu puntuacion es:{props.score}</div>
      {props.result === "win" && (
        <Alert variant="success" onClose={() => setShow(false)} dismissible>
        <Alert.Heading>Ganaste!!!</Alert.Heading>
        <p>
          Asi se hace! Has ganado esta partida!
        </p>
      </Alert>
      )}
      {props.result === "lose" && (
        <Alert variant="danger">
        <Alert.Heading>No puede ser!</Alert.Heading>
        <p>
          Lamentablemente perdiste esta partida
        </p>
      </Alert>
      )}
      {props.result === "draw" && (
        <h2>
          Empataron! Tu Elegiste {props.myChoice} y el pc eligi√≥{" "}
          {props.pcChoice}
        </h2>
      )}
      <div className="row container mt-5">
        <div className="col">
          <Button
            variant="outline-primary"
            value="piedra"
            size="lg"
            active
            onClick={props.onAddOption}
          >
            Piedra
          </Button>
        </div>
        <div className="col">
          <Button
            variant="outline-primary"
            value="papel"
            onClick={props.onAddOption}
          >
            Papel
          </Button>
        </div>
        <div className="col">
          <Button
            variant="outline-primary"
            value="tijera"
            onClick={props.onAddOption}
          >
            Tijera
          </Button>
        </div>
      </div>
    </div>
  );
}

export default Cachipun;
